export const orderMoviesReducer = (state, payload) => {

    if(payload === 'ascFecha' || payload === 'ascPuntaje'){

        return state.movies.sort( (a, b) => {
            if( a.attack > b.attack ) return 1
            if( b.attack > a.attack ) return -1
            return 0
        })

    } else {
        
        return state.movies.sort( (a, b) => {
            if( a.attack > b.attack ) return -1
            if( a.attack > b.attack ) return 1
            return 0
        });

    }

}


