export const orderMoviesReducer = (state, payload) => {

    if(payload === 'ascFecha' || payload === 'ascPuntaje'){

        return state.movies.sort( (a, b) => {
            if( a.attack > b.attack ) return 1
            if( b.attack > a.attack ) return -1
            return 0
        })

    } else {
        
        return state.movies.sort( (a, b) => {
            if( a.attack > b.attack ) return -1
            if( a.attack > b.attack ) return 1
            return 0
        });

    }

}


export const filterMoviesReducer = (state, payload) => {
    
    if( payload === 'all' ){

        return state.allMovies
        
    } else {

        //return state.allMovies.filter( m => m.genre_ids.includes(payload) )

        var moviesFiltradas = [];
        for(let i = 0; i < state.allMovies.length; i++){

            for(let j = 0; j < payload.length; j++){

                if( state.allMovies[i].genre_ids.includes(payload[j]) ) {

                    return moviesFiltradas.push(state.allMovies[i]);
                
                }
                 
            }
        }

        return moviesFiltradas;

    }

    

}
