import React from "react";


const Filtros = ({genres, handleCheckbox, filterGenres, handlerSearch, search, order}) => {

    return(
        <div>
            <div>
                <input type='text' onChange={handlerSearch} value={search}/>
                <button>Buscar</button>
            </div>
            <div>
                <span>genres</span>
                <div>
                {
                    genres && genres.map( g => {
                        return (
                            <div key={`gg${g.id}`} className="item-genres">
                                <input 
                                    type="checkbox"
                                    name="genres"
                                    id={g.id}
                                    value={g.id}
                                    onChange={ (e) => handleCheckbox(e) }
                                    checked={ filterGenres && filterGenres.includes([g.id])}
                                    />
                                <label htmlFor={g.id}>
                                    {g.name}
                                </label>
                            </div>
                            )
                    })
                }
                </div>
            </div>
            <div>
                <span>order</span>
                <div>
                    <input type="button" value="Nuevas - Antiguas" name='order' onClick={handleOrder}/>
                    <input type="button" value="Antiguas - Nuevas" name='order'  onClick={handleOrder}/>
                    <input type="button" value="0 - 10 puntos" name='order'  onClick={handleOrder}/>
                    <input type="button" value="10 - 0 puntos" name='order'  onClick={handleOrder}/>
                </div>
            </div>
        </div>
    );
}

export default Filtros;
