import { GET_ALL_DATA, TIPO_PRODUCTO, ORIGEN_RECOGIDA, DOCUMENTO, PAQUETERIA } from '../actions/constant'

const inicialState = {

    data: {},
    envio: {},
    contacto: {}

}


export default function rootReducer( state = inicialState, action ){

    switch(action.type){

        case GET_ALL_DATA:

            return {
                ...state,
                data: action.payload   
            }

        case TIPO_PRODUCTO: 

            return {
                ...state,
                envio: {...state.envio, tipoProducto: action.payload }
            }
        
        case ORIGEN_RECOGIDA:

            return {
                ...state, 
                envio: { ...state.envio, ...action.payload}
            }

        case DOCUMENTO:
            return {
                ...state, 
                envio: { ...state.envio, ...action.payload}
            }

        case PAQUETERIA:

            return {
                ...state,
                envio: {...state.envio, ...action.payload}
            }

        default: 

            return {
                ...state
            }
            
    }

}
