import React, {useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMovies, filterMovie, searchMovie, orderMovie } from "../../redux/actions";
import Filtros from "./Filtros";
import styles from './Movies.module.css';

const Movies = () => {
    const dispatch = useDispatch();
    const {movies, genres} = useSelector(state => state);
    const [filterGenres, setFilterGenres] = useState([]);
    const [search, setSearch] = useState('');
    const [order, setOrder] = useState('');

    useEffect(()=>{

        dispatch(getMovies())

    },[dispatch])

    const handleCheckbox = (e) => {

        if( !e.target.checked ){

            let genresNewFiltrado = filterGenres.filter( g => g !== e.target.value );
            setFilterGenres(genresNewFiltrado);
            dispatch(filterMovie(genresNewFiltrado))

        } else {

            if(e.target.checked){
                
                setFilterGenres([...filterGenres, e.target.value]);
                dispatch(filterMovie([...filterGenres, e.target.value]))

            }

        }

    }

    const handlerSearch = (e) => {
        setSearch(e.target.value);
        dispatch(searchMovie(e.target.value))
    }

    const handleOrder = (e) => {
        setOrder(e.target.value)
        dispatch(orderMovie(e.target.value))
    }

    return(
        <div className={styles.movies}>
            <h1>Películas</h1>
            <Filtros
                genres = {genres}
                handleCheckbox = {handleCheckbox}
                handlerSearch={handlerSearch}
                search={search}
                order={order}
                handleOrder={handleOrder}
            />
            <div className={styles.cont_movies}>
                {
                    movies && movies.length !== 0 ? movies.map( movie => {
                        return (
                            <div key={movie.id} className={styles.movie}>
                                <h2>Título: {movie.title}</h2>
                                <div className={styles.cont_movie}>
                                    <div className={styles.img_movie}>
                                        <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}/>
                                    </div>
                                    {
                                        {/* "popularity": 57.28,
                                            "vote_count": 17472,
                                            "video": false,
                                            "poster_path": "/dc1fX265fZIIY5Hab8I7CdETyJy.jpg",
                                            "id": 278,
                                            "adult": false,
                                            "backdrop_path": "/iNh3BivHyg5sQRPP1KOkzguEX0H.jpg",
                                            "original_language": "en",
                                            "original_title": "The Shawshank Redemption",
                                            "genre_ids": [
                                            18,
                                            80
                                            ],
                                            "title": "Cadena perpetua",
                                            "vote_average": 8.7,
                                            "overview": "Acusado del asesinato de su mujer, Andrew Dufresne, tras ser condenado a cadena perpetua, es enviado a la prisión de Shawshank. Con el paso de los años conseguirá ganarse la confianza del director del centro y el respeto de sus compañeros presidiarios, especialmente de Red, el jefe de la mafia de los sobornos.",
                                            "release_date": "1994-09-23" */}
                                    }
                                </div>
                            </div>
                        )
                    })
                    : null
                }
            </div>
        </div>
    )
}

export default Movies;
