import React, {useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMovies } from "../../redux/actions";
import Filtros from "./Filtros";

const Movies = () => {
    const dispatch = useDispatch();
    const {movies, genres} = useSelector(state => state);
    const [filterGenres, setFilterGenres] = useState([]);

    useEffect(()=>{

        dispatch(getMovies())

    },[dispatch, movies])

    const handleCheckbox = (e) => {

        if( !e.target.checked ){

            let genresNewFiltrado = filterGenres.filter( g => g !== e.target.value );
            setFilterGenres(genresNewFiltrado);

        } else {

            if(e.target.checked){
                
                setFilterGenres([...filterGenres, e.target.value]);

            }

          
        }

        console.log({filterGenres})
        
    }

    return(
        <div>
            <Filtros
                genres = {genres}
                handleCheckbox = {handleCheckbox}
            />
            {
                movies && movies.map( movie => {
                    return (
                        <div>
                            <span>TÃ­tulo: {movie.original_title}</span>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Movies;
